const { apiKey, storeId, fileName, mimeType, content } = $json;
if (!apiKey) return [{ json: { status: 'ERROR', error: 'apiKey required' } }];
if (!storeId) return [{ json: { status: 'ERROR', error: 'storeId required' } }];
if (!fileName) return [{ json: { status: 'ERROR', error: 'fileName required' } }];
if (!content) return [{ json: { status: 'ERROR', error: 'content (base64) required' } }];

// Extract store ID from full name format (e.g., "fileSearchStores/abc123" -> "abc123")
const extractedStoreId = storeId.includes('/') ? storeId.split('/').pop() : storeId;

const buffer = Buffer.from(content, 'base64');
const contentLength = buffer.length;

async function upload() {
  // Step 1: Initiate resumable upload using uploadToFileSearchStore endpoint
  const start = await this.helpers.httpRequest({
    method: 'POST',
    url: `https://generativelanguage.googleapis.com/upload/v1beta/fileSearchStores/${extractedStoreId}:uploadToFileSearchStore?key=${apiKey}`,
    headers: {
      'X-Goog-Upload-Protocol': 'resumable',
      'X-Goog-Upload-Command': 'start',
      'X-Goog-Upload-Header-Content-Length': contentLength.toString(),
      'X-Goog-Upload-Header-Content-Type': mimeType || 'text/plain',
      'Content-Type': 'application/json'
    },
    body: { displayName: fileName, mimeType: mimeType || 'text/plain' },
    json: true,
    resolveWithFullResponse: true,
    simple: false
  });

  if (!start.headers || !start.headers['x-goog-upload-url']) {
    return [{ json: { status: 'ERROR', error: 'Upload initiation failed', response: start.body } }];
  }

  const uploadUrl = Array.isArray(start.headers['x-goog-upload-url']) ? start.headers['x-goog-upload-url'][0] : start.headers['x-goog-upload-url'];

  // Step 2: Upload and finalize
  const uploaded = await this.helpers.httpRequest({
    method: 'POST',
    url: uploadUrl,
    headers: {
      'Content-Length': contentLength.toString(),
      'X-Goog-Upload-Offset': '0',
      'X-Goog-Upload-Command': 'upload, finalize'
    },
    body: buffer,
    encoding: null,
    resolveWithFullResponse: true,
    simple: false
  });

  const responseBody = uploaded.body ? (() => { try { return JSON.parse(uploaded.body); } catch { return {}; } })() : {};
  if (uploaded.statusCode && uploaded.statusCode >= 400) {
    return [{ json: { status: 'ERROR', error: `Upload failed (${uploaded.statusCode})`, body: responseBody } }];
  }

  // Extract operation/document ID from response
  const operationId = responseBody.name;

  return [{ json: { status: 'SUCCESS', operationId: operationId, fileName: fileName, operation: responseBody } }];
}

return upload.call(this);
